package Assingment;

public class Largestamong3nos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=3;
		int b=50;
		int c=10;
		
		if(a>b&&a>c) {
			System.out.println("A is greater");
		}
		else if(b>c) {
			System.out.println("B is greater");
		}
		else {
			System.out.println("C is greater");
		}
	}

}
