package Assingment;

public class Sumofevenoddnos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a, sum=0;
		for(a=2;a<=20;a=a+2) {
			sum=sum+a;
			System.out.println("SUm of even nos :"+sum);
		}
		
		
		
		int b, sum1=0;
		for(b=1;b<=20;b=b+2) {
			sum1=sum1+b;
			System.out.println("Sum of odd nos :"+sum1);
		}
		

	}

}
